    private void btn00ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn00ActionPerformed
        String[] split = bilangan.split(" ");
        
        if("".equals(bilangan)){
            bilangan += "0";
            txtOperand1.setText(bilangan);
        }else if(bilangan.equals("0")){
            txtOperand1.setText(bilangan);
        }else if(split.length > 1){
            String last = split[split.length-1];
            if("0".equals(last)){
                txtOperand1.setText(bilangan);
            }else{
                bilangan += "00";
                txtOperand1.setText(bilangan); 
            }
        }else{
            bilangan += "00";
            txtOperand1.setText(bilangan); 
        }
    }//GEN-LAST:event_btn00ActionPerformed

    private void btnTitikActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTitikActionPerformed
        String[] split = bilangan.split(" ");
        
        if(split.length>1){
            String last = split[split.length-1];
            String lastChar = bilangan.substring(bilangan.length()-1);
            if("+".equals(last) || "-".equals(last) || "/".equals(last) || "*".equals(last)){
                bilangan += ".";
                txtOperand1.setText(bilangan);
            }else if(".".equals(lastChar)){
                txtOperand1.setText(bilangan);
            }else{
                bilangan += ".";
                txtOperand1.setText(bilangan);
            }
        }else{
            bilangan += ".";
            txtOperand1.setText(bilangan);
        }
    }//GEN-LAST:event_btnTitikActionPerformed

