    private void btnTambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTambahActionPerformed
        String[] split = bilangan.split(" ");
        
        if("".equals(bilangan)){
            txtOperand1.setText(bilangan);
        }else if(split.length>1){
            String last = split[split.length-1];
            String lastChar = bilangan.substring(bilangan.length()-1);
            if("-".equals(lastChar) || "/".equals(lastChar) || "*".equals(lastChar)){
                String[] newbilangan = Arrays.copyOf(split, split.length-1);
                bilangan = String.join(" ", newbilangan);
                bilangan += " + ";
                txtOperand1.setText(bilangan);
            }else if("+".equals(last) || ".".equals(last)){
                txtOperand1.setText(bilangan);
            }else{
                bilangan += " + ";
                txtOperand1.setText(bilangan);
            }
        }else{
            bilangan += " + ";
            txtOperand1.setText(bilangan);
        }
    }//GEN-LAST:event_btnTambahActionPerformed

    private void btnKurangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnKurangActionPerformed
       String[] split = bilangan.split(" ");
       
       if(split.length>1){
           String last = split[split.length-1];
           if("+".equals(last)){
               String[] newbilangan = Arrays.copyOf(split, split.length-1);
               bilangan = String.join(" ", newbilangan);
               bilangan += " - ";
               txtOperand1.setText(bilangan);
           }else if("/".equals(last) || "*".equals(last)){
               bilangan += "-";
               txtOperand1.setText(bilangan);
           }else if("-".equals(last) || ".".equals(last)){
               txtOperand1.setText(bilangan);
           }else{
               bilangan += " - ";
               txtOperand1.setText(bilangan);
           }
        }else if("".equals(bilangan)){
            bilangan += "-";
            txtOperand1.setText(bilangan);
       }else{
           bilangan += " - ";
           txtOperand1.setText(bilangan);
       }
    }//GEN-LAST:event_btnKurangActionPerformed

