    private void btnHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHitungActionPerformed
        String bulat;
        String[] split = bilangan.split(" ");
        
        for(int i = 0; i<split.length; i++){
            if("+".equals(split[i]) || "-".equals(split[i]) || "/".equals(split[i]) || "*".equals(split[i])){
                bil = Double.parseDouble(split[i+1]);
                switch (split[i]){
                    case "+":
                        hasil += bil;
                        break;
                    case "-":
                        hasil -= bil;
                        break;
                    case "/":
                        hasil /= bil;
                        break;
                    case "*":
                        hasil *= bil;
                        break;
                }
                i++;
            }else{
                bil = Double.parseDouble(split[i]);
                hasil += bil;
            }
        }
        
        end = Double.toString(hasil);
        if(hasil % 1 == 0){
            bulat = end.substring(0,end.lastIndexOf("."));
            txtOperand2.setText(bulat);
        }else{
            txtOperand2.setText(end); 
        }
    }//GEN-LAST:event_btnHitungActionPerformed

